mapper.select:
select name as teachername,remark as teacherIntroduce ,pictureURL ,loginname from sys_user where 
id=(select userid from zonekey_curriculum where 
id=#{curriculumid}
resourcefloder=#{floder}
limit 1)

select id,resourceid,worksid,userid,content,score,templateid,reviewdate,remark,isover,createdate,type from study_review_detail where userid=#{userid} 
and resourceid=#{floder}
and resourceid=#{curriculumid}
and worksid=#{worksid}

mapper.update:
update study_review_detail set

		score = #{score},


		remark = #{remark},


		content = #{content},

reviewdate=sysDate(),isover='1'
where 1=1 

	and userid = #{userid}


	and resourceid = #{resourceid}


	and	worksid = #{worksid}

update study_record set

		content = #{content},

createdate=sysDate()
where 1=1 

	and	userid = #{userid}


	and	resourceid = #{resourceid}


	and	worksid = #{worksid}

mapper.select:
select id,resourceid,worksid,userid,content,templateid,createdate,type from study_record where userid=#{userid} 
and resourceid=#{floder}
and resourceid=#{curriculumid}
and worksid=#{worksid}
