mapper.select:
select id,name,CONCAT(#{_ip},picture) picture, Date_format(regbegintime,'%Y-%m-%d')regbegintime,Date_format(regendtime,'%Y-%m-%d')regendtime,Date_format(conbegintime,'%Y-%m-%d')conbegintime,Date_format(conendtime,'%Y-%m-%d')conendtime,description,createdate, (CASE WHEN sysDate() > conendtime THEN '4' when (date_format(sysDate(),'%Y-%m-%d') between regbegintime and regendtime) and (date_format(sysDate(),'%Y-%m-%d') between conbegintime and conendtime) then '2' when date_format(sysDate(),'%Y-%m-%d') between conbegintime and conendtime then '3' when date_format(sysDate(),'%Y-%m-%d') between regbegintime and regendtime THEN '1' else '5' end )status from study_active where 1=1
and type = #{keywords.value}
and name like concat('%',#{keywords.name},'%')
order by status,createdate desc
limit #{offset},#{pageSize}

select count(*) from study_active where 1=1


		and type = #{keywords.value}


		and name like concat('%',#{keywords.name},'%')
